"use strict";

var PostThreadVC_Upload = {
	uploadImage:function(imagePath){
		this.model.set('isUploading', true);
        var options = new FileUploadOptions();
        options.fileKey="image";
        options.fileName=imagePath.substr(imagePath.lastIndexOf('/')+1);
        options.mimeType="image/png";

        this.ft = new FileTransfer();

        try {

        	this.onProgress({
        		loaded:0,
        		total:1
        	});
            this.ft.onprogress = _.bind(this.onProgress, this);
            
            this.ft.upload(
                imagePath,
                encodeURI(conf.server.base_url+'save_image'),
                _.bind(this.onSuccess,this),
                _.bind(this.onFail,this),
                options
            );

        } catch(e) {
            this.onFail(e);
        }
	},
	onProgress:function(progressEvent){
		var $uploadOverlay = this.$el.find('#input-wrap .upload-overlay');
		if ($uploadOverlay.length==0) {
			$uploadOverlay = $('<div class="upload-overlay"></div>').appendTo('#input-wrap');
		}

		var percent = 100 - ((progressEvent.loaded / progressEvent.total) * 100);
		this.percent = percent;
		$uploadOverlay.css('width', percent+'%');
	},
	onSuccess:function(rawResponse){
		this.$el.find('#input-wrap .upload-overlay').remove();
		this.model.set('isUploading', false);
		try {
			if (rawResponse.responseCode==200) {
				var body = JSON.parse(rawResponse.response);
				this.model.set('imageUrl', conf.server.base_url+body.imageUrl)
			} else {
				this.onFail('Response is '+rawResponse.responseCode)
			}

		} catch (err) {
			this.onFail('JSON exception :('+ err);
		}
	},
	onFail:function(error){
		console.log("error",error);
	},
	isUploading:function(){
		return this.model.get('isUploading')==true;
	},
	abortFileTransfer:function(){
		this.$el.find('.upload-overlay').remove();
		this.ft.abort();
	}
};
"use strict";



// helper methods : gives from a percentage the appropriate onDragUpOrDownUpdateColor
function getColorFromPercentage(percentage) {
	var from255To0=function(percentage){
		var modulo = percentage % (100/6);
		var a = -2.55;
		var b = 255;
		var x = modulo * 6;
		return parseInt(a*x+b,10);
	};
	var fromZeroTo255=function(percentage){
		var modulo = percentage % (100/6);
		var a = 2.55;
		var b = 0;
		var x = modulo * 6;
		return parseInt(a*x+b,10);
	};
	var aSixth = 100/6;
	var model = [
		{ from:0, 		to:aSixth, 		r:255, 			g:0, 			b:fromZeroTo255 },
		{ from:aSixth, 	to:2*aSixth, 	r:from255To0, 	g:0, 			b:255 			},
		{ from:2*aSixth,to:3*aSixth, 	r:0, 			g:fromZeroTo255,b:255 			},
		{ from:3*aSixth,to:4*aSixth, 	r:0, 			g:255,			b:from255To0	},
		{ from:4*aSixth,to:5*aSixth, 	r:fromZeroTo255,g:255,			b:0				},
		{ from:5*aSixth,to:100,			r:255,			g:from255To0,	b:0				}
	];

	var step = _.reject(model, function(m){ return !(percentage >= m.from && percentage <= m.to) })[0];

	var color = {
		r: (typeof(step.r)=='function')?step.r(percentage):step.r,
		g: (typeof(step.g)=='function')?step.g(percentage):step.g,
		b: (typeof(step.b)=='function')?step.b(percentage):step.b
	}

	return 'rgb('+color.r+','+color.g+','+color.b+')'
}

function getPoliceFromPercentage(percentage) {
	var aSeventh = 100/7;
	var model = [
		{ from:0,			to:aSeventh,		name:'dragon',			size:'12px'},
		{ from:aSeventh,	to:2*aSeventh,		name:'wolf',			size:'40px'},
		{ from:2*aSeventh,	to:3*aSeventh,		name:'homizio',			size:'20px'},
		{ from:3*aSeventh,	to:4*aSeventh,		name:'typewriterforme',	size:'20px'},
		{ from:4*aSeventh,	to:5*aSeventh,		name:'bombard',			size:'20px'},
		{ from:5*aSeventh,	to:6*aSeventh,		name:'edmunds',			size:'20px'},
		{ from:6*aSeventh,	to:100,				name:'dienasty',		size:'20px'}
	];
	
	var font = _.reject(model, function(m){ return !(percentage >= m.from && percentage <= m.to) })[0];

	console.log('font for percentage : ', font.name, percentage);

	return {
		font:font.name,
		size:font.size
	}
}




