"use strict";

var ThreadModel = Backbone.Model.extend({
	url:conf.server.base_url+'post_thread',
	initialize:function(){
		this.on('sync', _.bind(this.subscribeToNewCommentsPush, this));
	},
	subscribeToNewCommentsPush:function(){
		this.set('isSaving',false);

		// TODO : 2 channel : 1 pour le créateur et 1 pour les participants
		cordova.exec(function(){
			console.log('success subscribeToNewCommentsPush');
		}, function(){
			console.warn('error subscribeToNewCommentsPush');
		}, 'PushNotification', 'subscribe', ['newcomments'+this.id]);	
		
	
	},
	report:function(text){
		var report = new ReportModel({
			thread_id:this.get('id'),
			text:text
		});
		report.save();
		app.threads.report(this.get('id'));
	},
	defaults:{
		title:'',
		comments_count:0,
		delete_me:false,
		brand_new:false
	}
});
"use strict";

var ThreadsCollection = Backbone.Collection.extend({
	model:ThreadModel,
	url:conf.server.base_url+'threads',
	initialize:function(){
		var saved = localStorage.getItem('threads_saved');
		if (saved!=null) {
			var parsed = JSON.parse(saved);
			if (this.length==0)
				this.add(parsed);
		}
		this.on('all', _.bind(this.saveThreads, this));
	},
	saveThreads:function(){
		// on retire ceux qui sont reported localement
		var reported = localStorage.getItem('reported');
		if (reported!=null) {
			var reports = JSON.parse(reported);
			for (var i = reports.length - 1; i >= 0; i--) {
				this.remove(reports[i]);
			}
		}

		localStorage.setItem('threads_saved', JSON.stringify(this.toJSON()));
	},
	comparator:function(a, b) {
	  if (a.get('last_comment') > b.get('last_comment')) return -1;
	  if (b.get('last_comment') > a.get('last_comment')) return 1; 
	  return 0; 
	},
	report:function(thread_id) {
		if (thread_id) {
			var reported = localStorage.getItem('reported');
			var	report = [];

			if (reported!=null) 
				report = JSON.parse(reported);
			report.push(thread_id);
			localStorage.setItem('reported', JSON.stringify(report));
			
			this.remove(thread_id);
		}
	}
});
